INSERT INTO texts VALUES(159,replace('**Nirukta** (निरुक्त) - **Yāska''s Nirukta** (यास्कनिरुक्तम्)\n**IAST Transliteration:** *Nirukta* - *Yāskaniruktam*','\n',char(10)),'Vedangas',NULL,NULL,NULL,NULL,NULL,'completed','2025-11-22',NULL,NULL,'2025-11-23 03:41:18','2025-11-23 03:41:18');
