PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE texts (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                name TEXT NOT NULL UNIQUE,
                category TEXT NOT NULL,
                subcategory TEXT,
                language TEXT,
                approximate_date TEXT,
                tradition TEXT,
                source_url TEXT,
                study_status TEXT DEFAULT 'not_started',
                study_date TEXT,
                file_path TEXT,
                notes TEXT,
                created_at TEXT DEFAULT CURRENT_TIMESTAMP,
                updated_at TEXT DEFAULT CURRENT_TIMESTAMP
            );
CREATE TABLE concepts (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                text_id INTEGER,
                concept_name TEXT NOT NULL,
                sanskrit_term TEXT,
                definition TEXT,
                context TEXT,
                practical_application TEXT, etymology TEXT, philosophical_context TEXT, category TEXT,
                FOREIGN KEY (text_id) REFERENCES texts(id)
            );
CREATE TABLE verses (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                text_id INTEGER,
                chapter TEXT,
                verse_number TEXT,
                sanskrit_text TEXT,
                transliteration TEXT,
                translation TEXT,
                commentary TEXT,
                significance TEXT, word_by_word_analysis TEXT, commentary_summary TEXT,
                FOREIGN KEY (text_id) REFERENCES texts(id)
            );
CREATE TABLE cross_references (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                source_text_id INTEGER,
                target_text_id INTEGER,
                connection_type TEXT,
                description TEXT,
                FOREIGN KEY (source_text_id) REFERENCES texts(id),
                FOREIGN KEY (target_text_id) REFERENCES texts(id)
            );
CREATE TABLE sanskrit_glossary (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                term TEXT NOT NULL UNIQUE,
                transliteration TEXT,
                literal_meaning TEXT,
                contextual_meaning TEXT,
                related_terms TEXT,
                usage_examples TEXT
            );
CREATE TABLE sanskrit_terms (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    term TEXT NOT NULL UNIQUE,
    sanskrit TEXT,
    meaning TEXT,
    source_text TEXT,
    usage_context TEXT
);
CREATE TABLE study_progress (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    text_name TEXT NOT NULL UNIQUE,
    status TEXT DEFAULT 'not_started',
    start_date DATE,
    completion_date DATE,
    notes TEXT
);
CREATE TABLE commentators (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            name TEXT NOT NULL,
            school TEXT,
            period TEXT,
            bio TEXT,
            created_at TEXT DEFAULT CURRENT_TIMESTAMP
        , sanskrit_name TEXT, bio_summary TEXT);
CREATE TABLE commentaries (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            verse_id INTEGER NOT NULL,
            commentator_id INTEGER NOT NULL,
            commentary_text TEXT,
            commentary_analysis TEXT,
            created_at TEXT DEFAULT CURRENT_TIMESTAMP,
            FOREIGN KEY (verse_id) REFERENCES verses(id),
            FOREIGN KEY (commentator_id) REFERENCES commentators(id)
        );
CREATE TABLE themes (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            name TEXT NOT NULL UNIQUE,
            description TEXT,
            created_at TEXT DEFAULT CURRENT_TIMESTAMP
        );
CREATE TABLE verse_to_concept (
            verse_id INTEGER NOT NULL,
            concept_id INTEGER NOT NULL,
            PRIMARY KEY (verse_id, concept_id),
            FOREIGN KEY (verse_id) REFERENCES verses(id),
            FOREIGN KEY (concept_id) REFERENCES concepts(id)
        );
CREATE TABLE verse_to_theme (
            verse_id INTEGER NOT NULL,
            theme_id INTEGER NOT NULL,
            PRIMARY KEY (verse_id, theme_id),
            FOREIGN KEY (verse_id) REFERENCES verses(id),
            FOREIGN KEY (theme_id) REFERENCES themes(id)
        );
CREATE TABLE categories (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL UNIQUE,
    parent_id INTEGER,
    level INTEGER NOT NULL,  -- 1=main, 2=sub, 3=sub-sub
    display_order INTEGER,
    description TEXT,
    target_breadth_score INTEGER DEFAULT 10,
    target_depth_score INTEGER DEFAULT 8,
    created_at TEXT DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (parent_id) REFERENCES categories(id)
);
CREATE TABLE subcategories (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    category_id INTEGER NOT NULL,
    name TEXT NOT NULL,
    description TEXT,
    expected_text_count INTEGER,  -- How many texts should be in this subcategory
    target_depth_score INTEGER DEFAULT 8,
    created_at TEXT DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (category_id) REFERENCES categories(id)
);
CREATE TABLE progress_tracking (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    text_id INTEGER,
    category_id INTEGER,
    subcategory_id INTEGER,
    breadth_score INTEGER DEFAULT 0,
    depth_score INTEGER DEFAULT 0,
    combined_score REAL DEFAULT 0.0,
    last_updated TEXT DEFAULT CURRENT_TIMESTAMP,
    notes TEXT,
    FOREIGN KEY (text_id) REFERENCES texts(id),
    FOREIGN KEY (category_id) REFERENCES categories(id),
    FOREIGN KEY (subcategory_id) REFERENCES subcategories(id)
);
CREATE TABLE depth_criteria (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    text_id INTEGER NOT NULL,
    criterion_name TEXT NOT NULL,  -- e.g., "verse_analysis", "commentary_comparison"
    criterion_met BOOLEAN DEFAULT 0,
    evidence_file_path TEXT,
    last_checked TEXT DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (text_id) REFERENCES texts(id)
);
CREATE TABLE gap_analysis (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    category_id INTEGER,
    subcategory_id INTEGER,
    text_id INTEGER,
    gap_type TEXT NOT NULL,  -- "breadth", "depth", "synthesis"
    priority_score REAL,  -- Calculated priority
    recommendation TEXT,
    created_at TEXT DEFAULT CURRENT_TIMESTAMP,
    resolved BOOLEAN DEFAULT 0,
    FOREIGN KEY (category_id) REFERENCES categories(id),
    FOREIGN KEY (subcategory_id) REFERENCES subcategories(id),
    FOREIGN KEY (text_id) REFERENCES texts(id)
);
CREATE TABLE session_log (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    session_date TEXT NOT NULL,
    session_number INTEGER,
    focus_area TEXT,
    texts_studied TEXT,  -- JSON array of text IDs
    breadth_delta REAL,  -- Change in overall breadth
    depth_delta REAL,    -- Change in overall depth
    notes TEXT,
    created_at TEXT DEFAULT CURRENT_TIMESTAMP
);
CREATE TABLE verse_concepts (
    verse_id INTEGER NOT NULL,
    concept_id INTEGER NOT NULL,
    relevance_score INTEGER DEFAULT 5,
    notes TEXT,
    PRIMARY KEY (verse_id, concept_id),
    FOREIGN KEY (verse_id) REFERENCES verses(verse_id),
    FOREIGN KEY (concept_id) REFERENCES concepts(concept_id)
);
DELETE FROM sqlite_sequence;
CREATE INDEX idx_progress_text ON progress_tracking(text_id);
CREATE INDEX idx_progress_category ON progress_tracking(category_id);
CREATE INDEX idx_depth_criteria_text ON depth_criteria(text_id);
CREATE INDEX idx_gap_analysis_priority ON gap_analysis(priority_score DESC);
CREATE INDEX idx_gap_analysis_resolved ON gap_analysis(resolved);
CREATE INDEX idx_verses_text_id ON verses(text_id);
CREATE INDEX idx_verses_chapter ON verses(chapter);
CREATE INDEX idx_verses_verse_number ON verses(verse_number);
CREATE INDEX idx_commentaries_verse_id ON commentaries(verse_id);
CREATE INDEX idx_commentaries_commentator_id ON commentaries(commentator_id);
CREATE INDEX idx_verse_concepts_verse ON verse_concepts(verse_id);
CREATE INDEX idx_verse_concepts_concept ON verse_concepts(concept_id);
CREATE VIEW verse_commentary_count AS
SELECT 
    v.verse_id,
    v.text_id,
    t.title AS text_title,
    v.chapter,
    v.verse_number,
    COUNT(c.commentary_id) AS commentary_count
FROM verses v
LEFT JOIN commentaries c ON v.verse_id = c.verse_id
LEFT JOIN texts t ON v.text_id = t.id
GROUP BY v.verse_id;
CREATE VIEW verse_cross_ref_count AS
SELECT 
    v.verse_id,
    v.text_id,
    t.title AS text_title,
    v.chapter,
    v.verse_number,
    COUNT(DISTINCT cr1.cross_ref_id) + COUNT(DISTINCT cr2.cross_ref_id) AS cross_ref_count
FROM verses v
LEFT JOIN cross_references cr1 ON v.verse_id = cr1.source_verse_id
LEFT JOIN cross_references cr2 ON v.verse_id = cr2.target_verse_id
LEFT JOIN texts t ON v.text_id = t.id
GROUP BY v.verse_id;
CREATE VIEW text_depth_metrics AS
SELECT 
    t.id AS text_id,
    t.title,
    t.category,
    COUNT(DISTINCT v.verse_id) AS total_verses,
    SUM(CASE WHEN v.translation IS NOT NULL THEN 1 ELSE 0 END) AS verses_with_translation,
    SUM(CASE WHEN v.translation IS NOT NULL THEN 1 ELSE 0 END) * 100.0 / COUNT(DISTINCT v.verse_id) AS translation_coverage_pct,
    AVG(vcc.commentary_count) AS avg_commentaries_per_verse,
    AVG(vcrc.cross_ref_count) AS avg_cross_refs_per_verse
FROM texts t
LEFT JOIN verses v ON t.id = v.text_id
LEFT JOIN verse_commentary_count vcc ON v.verse_id = vcc.verse_id
LEFT JOIN verse_cross_ref_count vcrc ON v.verse_id = vcrc.verse_id
GROUP BY t.id;
COMMIT;
